// –ü–∞—Ä—Å–µ—Ä RSS –¥–ª—è 3DNews —Å —Ä–∞–±–æ—á–µ–π —Ñ—É–Ω–∫—Ü–∏–µ–π –ø–æ–∏—Å–∫–∞ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π
const input = $input.first();

if (!input || !input.binary || !input.binary.data || !input.binary.data.data) {
  return [{ json: { error: 'No binary data received' } }];
}

try {
  // –î–µ–∫–æ–¥–∏—Ä—É–µ–º Base64 –≤ —Å—Ç—Ä–æ–∫—É
  const base64Data = input.binary.data.data;
  const xmlString = Buffer.from(base64Data, 'base64').toString('utf-8');

  // –ò—â–µ–º –≤—Å–µ item —ç–ª–µ–º–µ–Ω—Ç—ã
  const itemMatches = xmlString.match(/<item>[\s\S]*?<\/item>/gi);

  if (!itemMatches || itemMatches.length === 0) {
    return [{ json: { error: 'No news items found in RSS' } }];
  }

  console.log(`üéØ –ù–ê–ß–ê–õ–û –ü–ê–†–°–ò–ù–ì–ê RSS`);
  console.log(`–í—Å–µ–≥–æ —ç–ª–µ–º–µ–Ω—Ç–æ–≤ <item> –≤ RSS: ${itemMatches.length}`);

  const results = [];
  const statistics = {
    total: itemMatches.length,
    added: 0,
    skipped: {
      noLink: 0,
      duplicateInRss: 0,
      tooOld: 0,
      noTitle: 0,
      dateParseError: 0
    }
  };

  // –ú–Ω–æ–∂–µ—Å—Ç–≤–æ –¥–ª—è –æ—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏—è –æ–±—Ä–∞–±–æ—Ç–∞–Ω–Ω—ã—Ö —Å—Å—ã–ª–æ–∫ –≤ –≠–¢–û–ú RSS
  const processedLinksInThisRSS = new Set();

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –∏–∑–≤–ª–µ—á–µ–Ω–∏—è —Å–æ–¥–µ—Ä–∂–∏–º–æ–≥–æ —Ç–µ–≥–∞
  function getTagContent(xml, tagName) {
    const regex = new RegExp('<' + tagName + '[^>]*>([\\s\\S]*?)<\\/' + tagName + '>', 'i');
    const match = xml.match(regex);
    if (match && match[1]) {
      let content = match[1].trim();
      // –£–±–∏—Ä–∞–µ–º CDATA
      content = content.replace(/<!\[CDATA\[(.*?)\]\]>/g, '$1');
      // –î–µ–∫–æ–¥–∏—Ä—É–µ–º HTML entities
      content = content.replace(/&lt;/g, '<')
                       .replace(/&gt;/g, '>')
                       .replace(/&amp;/g, '&')
                       .replace(/&quot;/g, '"')
                       .replace(/&apos;/g, "'")
                       .replace(/&nbsp;/g, ' ')
                       .replace(/&mdash;/g, '‚Äî')
                       .replace(/&laquo;/g, '¬´')
                       .replace(/&raquo;/g, '¬ª');
      return content;
    }
    return '';
  }

  // –í–û–¢ –†–ê–ë–û–ß–ê–Ø –§–£–ù–ö–¶–ò–Ø –î–õ–Ø –ü–û–ò–°–ö–ê –ò–ó–û–ë–†–ê–ñ–ï–ù–ò–ô (–∏–∑ —Å—Ç–∞—Ä–æ–≥–æ –∫–æ–¥–∞)
  function getImageUrl(xml) {
    // –í–∞—Ä–∏–∞–Ω—Ç 1: –ü—Ä—è–º–æ–π –ø–æ–∏—Å–∫ enclosure —Ç–µ–≥–∞ –∫–∞–∫ –≤ –≤–∞—à–µ–º –ø—Ä–∏–º–µ—Ä–µ
    // <enclosure url="https://cdn.3dnews.ru/assets/external/illustrations/2026/01/26/1135849/00.jpg" length="371553" type="image/jpeg" />

    // –ò—â–µ–º –ø–æ —Ç–æ—á–Ω–æ–º—É —à–∞–±–ª–æ–Ω—É
    const directRegex = /<enclosure\s+url\s*=\s*["']([^"']+)["'][^>]*>/i;
    const directMatch = xml.match(directRegex);

    if (directMatch) {
      console.log('Found direct enclosure match:', directMatch[1].substring(0, 60) + '...');
      return directMatch[1];
    }

    // –í–∞—Ä–∏–∞–Ω—Ç 2: –ë–æ–ª–µ–µ –≥–∏–±–∫–∏–π –ø–æ–∏—Å–∫
    const flexibleRegex = /<enclosure[^>]+url=["']([^"']+\.(?:jpg|jpeg|png|gif|webp))["'][^>]*>/i;
    const flexibleMatch = xml.match(flexibleRegex);

    if (flexibleMatch) {
      console.log('Found flexible enclosure match:', flexibleMatch[1].substring(0, 60) + '...');
      return flexibleMatch[1];
    }

    // –í–∞—Ä–∏–∞–Ω—Ç 3: –ò—â–µ–º –ª—é–±—É—é —Å—Ç—Ä–æ–∫—É, –ø–æ—Ö–æ–∂—É—é –Ω–∞ URL –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è –≤ item
    const urlRegex = /(https?:\/\/[^"\'\s<>]+\.(?:jpg|jpeg|png|gif|webp))/gi;
    const urlMatches = xml.match(urlRegex);

    if (urlMatches && urlMatches.length > 0) {
      console.log('Found image URL in text:', urlMatches[0].substring(0, 60) + '...');
      // –ë–µ—Ä–µ–º –ø–µ—Ä–≤—ã–π –Ω–∞–π–¥–µ–Ω–Ω—ã–π URL –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è
      return urlMatches[0];
    }

    // –í–∞—Ä–∏–∞–Ω—Ç 4: –ò—â–µ–º –º–µ–¥–∏–∞ –∫–æ–Ω—Ç–µ–Ω—Ç
    const mediaRegex = /<media:content[^>]*url=["']([^"']+)["'][^>]*>/i;
    const mediaMatch = xml.match(mediaRegex);
    if (mediaMatch && mediaMatch[1]) {
      console.log('Found media content:', mediaMatch[1].substring(0, 60) + '...');
      return mediaMatch[1];
    }

    console.log('No image found in this item');
    return '';
  }

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –∏–∑–≤–ª–µ—á–µ–Ω–∏—è –∞—Ç—Ä–∏–±—É—Ç–∞ category domain
  function getCategoryDomain(xml) {
    const regex = /<category[^>]*domain=["']([^"']+)["'][^>]*>/i;
    const match = xml.match(regex);
    return match ? match[1] : '';
  }

  // –û–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º –í–°–ï —ç–ª–µ–º–µ–Ω—Ç—ã (–Ω–µ —Ç–æ–ª—å–∫–æ –ø–µ—Ä–≤—ã–µ 2)
  for (let i = 0; i < itemMatches.length; i++) {
    const itemXml = itemMatches[i];
    const itemNumber = i + 1;

    console.log(`\nüì∞ –≠–õ–ï–ú–ï–ù–¢ ${itemNumber}/${itemMatches.length}`);
    console.log(`----------------------------------------`);

    // –ü–∞—Ä—Å–∏–º –¥–∞–Ω–Ω—ã–µ
    const title = getTagContent(itemXml, 'title');
    const link = getTagContent(itemXml, 'link');
    const description = getTagContent(itemXml, 'description');
    const pubDate = getTagContent(itemXml, 'pubDate');
    const category = getTagContent(itemXml, 'category');
    const categoryDomain = getCategoryDomain(itemXml);
    const imageUrl = getImageUrl(itemXml); // –ò—Å–ø–æ–ª—å–∑—É–µ–º —Ä–∞–±–æ—á—É—é —Ñ—É–Ω–∫—Ü–∏—é

    console.log(`üìù –ó–∞–≥–æ–ª–æ–≤–æ–∫: ${title ? '‚úì ' + title.substring(0, 60) + '...' : '‚úó –ù–ï–¢'}`);
    console.log(`üîó –°—Å—ã–ª–∫–∞: ${link ? '‚úì ' + link.substring(0, 60) + '...' : '‚úó –ù–ï–¢'}`);
    console.log(`üìÖ –î–∞—Ç–∞: ${pubDate ? '‚úì ' + pubDate : '‚úó –ù–ï–¢'}`);
    console.log(`üñºÔ∏è –ò–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ: ${imageUrl ? '‚úì –ï–°–¢–¨' : '‚úó –ù–ï–¢'}`);
    if (imageUrl) {
      console.log(`   URL: ${imageUrl.substring(0, 80)}...`);
    }

    // ============ –®–ê–ì 1: –ü—Ä–æ–≤–µ—Ä–∫–∞ –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã—Ö –ø–æ–ª–µ–π ============
    if (!title) {
      console.log(`‚ùå –ü–†–û–ü–£–©–ï–ù–û: –ù–µ—Ç –∑–∞–≥–æ–ª–æ–≤–∫–∞`);
      statistics.skipped.noTitle++;
      continue;
    }

    if (!link) {
      console.log(`‚ùå –ü–†–û–ü–£–©–ï–ù–û: –ù–µ—Ç —Å—Å—ã–ª–∫–∏`);
      statistics.skipped.noLink++;
      continue;
    }

    // ============ –®–ê–ì 2: –ü—Ä–æ–≤–µ—Ä–∫–∞ –¥—É–±–ª–∏–∫–∞—Ç–æ–≤ –≤ –≠–¢–û–ú RSS ============
    if (processedLinksInThisRSS.has(link)) {
      console.log(`‚ùå –ü–†–û–ü–£–©–ï–ù–û: –î—É–±–ª–∏–∫–∞—Ç –≤ —ç—Ç–æ–º RSS (—É–∂–µ –æ–±—Ä–∞–±–∞—Ç—ã–≤–∞–ª–∏)`);
      statistics.skipped.duplicateInRSS++;
      continue;
    }
    processedLinksInThisRSS.add(link);

    // ============ –®–ê–ì 3: –ü—Ä–æ–≤–µ—Ä–∫–∞ –¥–∞—Ç—ã (–í–†–ï–ú–ï–ù–ù–û –û–¢–ö–õ–Æ–ß–ò–¢–ï!) ============
    // –ó–ê–ö–û–ú–ú–ï–ù–¢–ò–†–£–ô–¢–ï –≠–¢–û–¢ –ë–õ–û–ö –ù–ê –í–†–ï–ú–Ø –¢–ï–°–¢–ò–†–û–í–ê–ù–ò–Ø!
    /*
    let skipDueToDate = false;
    if (pubDate) {
      try {
        const newsDate = new Date(pubDate);
        const now = new Date();

        if (isNaN(newsDate.getTime())) {
          console.log(`‚ö†Ô∏è –ù–µ —É–¥–∞–ª–æ—Å—å —Ä–∞—Å–ø–∞—Ä—Å–∏—Ç—å –¥–∞—Ç—É: ${pubDate}`);
          statistics.skipped.dateParseError++;
        } else {
          const daysDiff = (now - newsDate) / (1000 * 60 * 60 * 24);
          console.log(`üìÜ –í–æ–∑—Ä–∞—Å—Ç –Ω–æ–≤–æ—Å—Ç–∏: ${daysDiff.toFixed(1)} –¥–Ω–µ–π`);

          // –£–í–ï–õ–ò–ß–¨–¢–ï –ò–õ–ò –£–ë–ï–†–ò–¢–ï –≠–¢–£ –ü–†–û–í–ï–†–ö–£!
          if (daysDiff > 30) { // –ò–∑–º–µ–Ω–∏—Ç–µ –Ω–∞ 30 –∏–ª–∏ –±–æ–ª—å—à–µ –¥–ª—è —Ç–µ—Å—Ç–∞
            console.log(`‚ùå –ü–†–û–ü–£–©–ï–ù–û: –°–ª–∏—à–∫–æ–º —Å—Ç–∞—Ä–∞—è (${daysDiff.toFixed(1)} –¥–Ω–µ–π)`);
            statistics.skipped.tooOld++;
            skipDueToDate = true;
          }
        }
      } catch (e) {
        console.log(`‚ö†Ô∏è –û—à–∏–±–∫–∞ –ø—Ä–∏ –æ–±—Ä–∞–±–æ—Ç–∫–µ –¥–∞—Ç—ã: ${e.message}`);
        statistics.skipped.dateParseError++;
      }
    }

    if (skipDueToDate) {
      continue;
    }
    */
    // ============ –ö–û–ù–ï–¶ –®–ê–ì–ê 3 ============

    // –û—á–∏—â–∞–µ–º –æ–ø–∏—Å–∞–Ω–∏–µ
    let cleanDescription = description;
    if (cleanDescription) {
      cleanDescription = cleanDescription.replace(/<[^>]*>/g, ' ');
      cleanDescription = cleanDescription.replace(/\s+/g, ' ').trim();
      cleanDescription = cleanDescription.substring(0, 1000);
    }

    // –û–ø—Ä–µ–¥–µ–ª—è–µ–º –∫–∞—Ç–µ–≥–æ—Ä–∏—é
    let finalCategory = '';
    if (category) {
      finalCategory = category;
    } else if (categoryDomain) {
      finalCategory = categoryDomain.replace('/', '');
    }

    // –£–ø—Ä–æ—â–∞–µ–º –∫–∞—Ç–µ–≥–æ—Ä–∏–∏
    if (finalCategory.includes('software')) finalCategory = 'Software';
    else if (finalCategory.includes('hardware')) finalCategory = 'Hardware';
    else if (finalCategory.includes('storage')) finalCategory = 'Storage';
    else if (finalCategory.includes('games')) finalCategory = 'Games';
    else if (finalCategory.includes('–ù–æ–≤–æ—Å—Ç–∏')) finalCategory = finalCategory.replace('–ù–æ–≤–æ—Å—Ç–∏ ', '');

    // –ò—Å–ø–æ–ª—å–∑—É–µ–º guid –∫–∞–∫ –∞–ª—å—Ç–µ—Ä–Ω–∞—Ç–∏–≤–Ω—É—é —Å—Å—ã–ª–∫—É
    let finalLink = link;
    if (!finalLink) {
      finalLink = getTagContent(itemXml, 'guid');
    }

    // –§–æ—Ä–º–∞—Ç–∏—Ä—É–µ–º –¥–ª—è Airtable - –ü–†–û–°–¢–ê–Ø –°–¢–†–û–ö–ê –¥–ª—è ImageUrl
    results.push({
      json: {
        title: title || '',
        link: finalLink || '',
        description: cleanDescription || '',
        date: pubDate || '',
        select: finalCategory || '',
        imageUrl: imageUrl || ''  // –ü–†–û–°–¢–ê–Ø –°–¢–†–û–ö–ê, –Ω–µ –º–∞—Å—Å–∏–≤!
      }
    });

    statistics.added++;
    console.log(`‚úÖ –î–û–ë–ê–í–õ–ï–ù–û –í –†–ï–ó–£–õ–¨–¢–ê–¢–´!`);
  }

  // ============ –ò–¢–û–ì–ò ============
  console.log(`\nüìä üìä üìä –ò–¢–û–ì–ò –ü–ê–†–°–ò–ù–ì–ê üìä üìä üìä`);
  console.log(`========================================`);
  console.log(`üìà –í—Å–µ–≥–æ –≤ RSS: ${statistics.total}`);
  console.log(`‚úÖ –î–æ–±–∞–≤–ª–µ–Ω–æ: ${statistics.added}`);
  console.log(`‚ùå –ü—Ä–æ–ø—É—â–µ–Ω–æ: ${statistics.total - statistics.added}`);
  console.log(`\nüìã –î–µ—Ç–∞–ª–∏ –ø—Ä–æ–ø—É—â–µ–Ω–Ω—ã—Ö:`);
  console.log(`   ‚Ä¢ –ë–µ–∑ —Å—Å—ã–ª–∫–∏: ${statistics.skipped.noLink}`);
  console.log(`   ‚Ä¢ –ë–µ–∑ –∑–∞–≥–æ–ª–æ–≤–∫–∞: ${statistics.skipped.noTitle}`);
  console.log(`   ‚Ä¢ –î—É–±–ª–∏–∫–∞—Ç—ã –≤ RSS: ${statistics.skipped.duplicateInRSS}`);
  console.log(`   ‚Ä¢ –°–ª–∏—à–∫–æ–º —Å—Ç–∞—Ä—ã–µ: ${statistics.skipped.tooOld}`);
  console.log(`   ‚Ä¢ –û—à–∏–±–∫–∏ –¥–∞—Ç—ã: ${statistics.skipped.dateParseError}`);

  if (results.length === 0) {
    console.log(`\n‚ö†Ô∏è –í–ù–ò–ú–ê–ù–ò–ï: –ù–µ –¥–æ–±–∞–≤–ª–µ–Ω–æ –Ω–∏ –æ–¥–Ω–æ–π –Ω–æ–≤–æ—Å—Ç–∏!`);
    return [{ json: { 
      message: 'No news added after filtering',
      statistics: statistics,
      debug: 'Check logs for details'
    } }];
  }

  // –°—á–∏—Ç–∞–µ–º –Ω–æ–≤–æ—Å—Ç–∏ —Å –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è–º–∏
  const withImages = results.filter(item => item.json.imageUrl).length;
  console.log(`\nüéâ –£–°–ü–ï–•: ${results.length} –Ω–æ–≤–æ—Å—Ç–µ–π –≥–æ—Ç–æ–≤—ã –∫ –¥–æ–±–∞–≤–ª–µ–Ω–∏—é`);
  console.log(`üñºÔ∏è –ò–∑ –Ω–∏—Ö —Å –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è–º–∏: ${withImages}`);

  console.log(`\nüìã –ü–ï–†–í–´–ï 5 –ó–ê–ü–ò–°–ï–ô –î–õ–Ø –î–û–ë–ê–í–õ–ï–ù–ò–Ø:`);
  results.slice(0, 5).forEach((item, idx) => {
    const data = item.json;
    console.log(`${idx + 1}. ${data.title.substring(0, 70)}...`);
    console.log(`   –°—Å—ã–ª–∫–∞: ${data.link.substring(0, 60)}...`);
    console.log(`   –î–∞—Ç–∞: ${data.date}`);
    console.log(`   –ò–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ: ${data.imageUrl ? '‚úì ' + data.imageUrl.substring(0, 60) + '...' : '‚úó –ù–ï–¢'}`);
    console.log(`---`);
  });

  return results;

} catch (err) {
  console.error('‚ùå –ö–†–ò–¢–ò–ß–ï–°–ö–ê–Ø –û–®–ò–ë–ö–ê:', err);
